mapper.update:
UPDATE service_list 
SET SERVICE_SUMMARY=#{SERVICE_SUMMARY}, APPLY_METHOD=#{APPLY_METHOD}, 
	APPLY_DATE=#{APPLY_DATE}, AGENCY_CODE=#{AGENCY_CODE}, DEPT_NAME=#{DEPT_NAME}, 
	VIEWS=#{VIEWS}, AGENCY_TYPE=#{AGENCY_TYPE}, USER_CRITERA=#{USER_CRITERA}, 
	SERVICE_FIELD=#{SERVICE_FIELD}, PHONE=#{PHONE}
WHERE SERVICE_NAME = #{SERVICE_ID}
