mapper.select:
select service_id, service_name, service_summary, apply_date from service_list
where service_name like concat('%', #{keyword}, '%') or service_summary like concat('%', #{keyword}, '%') or service_field like concat('%', #{keyword}, '%')
limit #{offset}, #{amount}

select service_id, service_name, service_summary from service_list
order by views desc
limit #{count}
